import { Injectable, signal, computed, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { Observable, tap, catchError, of } from 'rxjs';
import { environment } from '@env/environment';

export interface User {
  id: string;
  email: string;
  name: string;
  profile?: string;
  roles: string[];
}

@Injectable({ providedIn: 'root' })
export class AuthService {
  private http = inject(HttpClient);
  private router = inject(Router);

  // Signals for reactive state
  private currentUserSignal = signal<User | null>(null);
  private isLoadingSignal = signal(false);
  private errorSignal = signal<string | null>(null);

  // Computed signals
  currentUser = this.currentUserSignal.asReadonly();
  isLoading = this.isLoadingSignal.asReadonly();
  error = this.errorSignal.asReadonly();
  isAuthenticated = computed(() => this.currentUserSignal() !== null);
  isUser = computed(() => this.currentUserSignal()?.roles.includes('user') ?? false);

  login(email: string, password: string): Observable<User> {
    this.isLoadingSignal.set(true);
    this.errorSignal.set(null);

    return this.http.post<User>(`${environment.apiUrl}/auth/sign-in`, { email, password })
      .pipe(
        tap(user => {
          this.currentUserSignal.set(user);
          this.isLoadingSignal.set(false);
        }),
        catchError(error => {
          this.errorSignal.set(error.message);
          this.isLoadingSignal.set(false);
          throw error;
        })
      );
  }

  getProfile(): Observable<User> {
    return this.http.get<User>(`${environment.apiUrl}/auth/profile`)
      .pipe(tap(user => this.currentUserSignal.set(user)));
  }

  logout(): Observable<any> {
    return this.http.post(`${environment.apiUrl}/auth/sign-out`, {})
      .pipe(
        tap(() => {
          this.currentUserSignal.set(null);
          this.router.navigate(['/login']);
        })
      );
  }
}

