import { Injectable, signal, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, tap } from 'rxjs';
import { environment } from '@env/environment';

export interface Venture {
  id: string;
  name: string;
  slug: string;
  description: string;
  problem_solved: string;
  target_market: string;
  logo?: string;
  cover?: string;
  sector?: string;
  stage?: string;
  is_published: boolean;
}

@Injectable({ providedIn: 'root' })
export class VenturesService {
  private http = inject(HttpClient);

  private venturesSignal = signal<Venture[]>([]);
  private selectedVentureSignal = signal<Venture | null>(null);
  private isLoadingSignal = signal(false);
  private totalCountSignal = signal(0);

  ventures = this.venturesSignal.asReadonly();
  selectedVenture = this.selectedVentureSignal.asReadonly();
  isLoading = this.isLoadingSignal.asReadonly();
  totalCount = this.totalCountSignal.asReadonly();

  getMyVentures(page = 1): Observable<[Venture[], number]> {
    this.isLoadingSignal.set(true);

    return this.http.get<[Venture[], number]>(
      `${environment.apiUrl}/ventures/by-user/paginated?page=${page}`
    ).pipe(
      tap(([ventures, count]) => {
        this.venturesSignal.set(ventures);
        this.totalCountSignal.set(count);
        this.isLoadingSignal.set(false);
      })
    );
  }

  getVentureBySlug(slug: string): Observable<Venture> {
    return this.http.get<Venture>(`${environment.apiUrl}/ventures/by-slug/${slug}`)
      .pipe(tap(venture => this.selectedVentureSignal.set(venture)));
  }

  createVenture(data: Partial<Venture>): Observable<Venture> {
    return this.http.post<Venture>(`${environment.apiUrl}/ventures`, data)
      .pipe(
        tap(venture => {
          this.venturesSignal.update(ventures => [venture, ...ventures]);
        })
      );
  }

  updateVenture(slug: string, data: Partial<Venture>): Observable<Venture> {
    return this.http.patch<Venture>(`${environment.apiUrl}/ventures/${slug}`, data)
      .pipe(
        tap(updated => {
          this.venturesSignal.update(ventures =>
            ventures.map(v => v.slug === slug ? updated : v)
          );
          this.selectedVentureSignal.set(updated);
        })
      );
  }

  deleteVenture(id: string): Observable<void> {
    return this.http.delete<void>(`${environment.apiUrl}/ventures/${id}`)
      .pipe(
        tap(() => {
          this.venturesSignal.update(ventures =>
            ventures.filter(v => v.id !== id)
          );
        })
      );
  }

  uploadLogo(ventureId: string, file: File): Observable<any> {
    const formData = new FormData();
    formData.append('logo', file);

    return this.http.post(
      `${environment.apiUrl}/ventures/add-logo/${ventureId}`,
      formData
    );
  }
}

