import { Component, input, output, effect, inject, signal } from '@angular/core';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { VenturesService, Venture } from '../services/ventures.service';

@Component({
  selector: 'app-venture-form',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  template: `
    <form [formGroup]="ventureForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="name">Venture Name *</label>
        <input id="name" formControlName="name" type="text" />
        @if (ventureForm.controls.name.invalid && ventureForm.controls.name.touched) {
          <span class="error">Name is required</span>
        }
      </div>

      <div class="form-group">
        <label for="description">Description *</label>
        <textarea id="description" formControlName="description"></textarea>
      </div>

      <div class="form-group">
        <label for="problem_solved">Problem Solved *</label>
        <textarea id="problem_solved" formControlName="problem_solved"></textarea>
      </div>

      <div class="form-group">
        <label for="target_market">Target Market *</label>
        <input id="target_market" formControlName="target_market" type="text" />
      </div>

      <button type="submit" [disabled]="ventureForm.invalid || isSubmitting()">
        {{ isSubmitting() ? 'Saving...' : 'Save Venture' }}
      </button>
    </form>
  `
})
export class VentureFormComponent {
  // Modern component inputs/outputs
  venture = input<Venture | null>(null);
  onSave = output<Venture>();

  private fb = inject(FormBuilder);
  private venturesService = inject(VenturesService);
  private router = inject(Router);

  isSubmitting = signal(false);

  ventureForm = this.fb.group({
    name: ['', Validators.required],
    description: ['', Validators.required],
    problem_solved: ['', Validators.required],
    target_market: ['', Validators.required],
    sector: [''],
    stage: [''],
    location: [''],
    email: ['', Validators.email],
    phone_number: [''],
    website: [''],
    linkedin_url: ['']
  });

  constructor() {
    // Effect to populate form when editing
    effect(() => {
      const ventureData = this.venture();
      if (ventureData) {
        this.ventureForm.patchValue(ventureData);
      }
    });
  }

  onSubmit(): void {
    if (this.ventureForm.valid) {
      this.isSubmitting.set(true);
      const formData = this.ventureForm.value;

      const request$ = this.venture()
        ? this.venturesService.updateVenture(this.venture()!.slug, formData)
        : this.venturesService.createVenture(formData);

      request$.subscribe({
        next: (venture) => {
          this.isSubmitting.set(false);
          this.onSave.emit(venture);
          this.router.navigate(['/dashboard/ventures']);
        },
        error: () => {
          this.isSubmitting.set(false);
        }
      });
    }
  }
}

