import { Component, inject, OnInit, signal, computed } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { VenturesService } from '../services/ventures.service';

@Component({
  selector: 'app-ventures-list',
  standalone: true,
  imports: [CommonModule, RouterLink],
  template: `
    <div class="ventures-container">
      <div class="header">
        <h1>My Ventures</h1>
        <a routerLink="/dashboard/ventures/create" class="btn-primary">
          Create Venture
        </a>
      </div>

      @if (venturesService.isLoading()) {
        <div class="loading">Loading ventures...</div>
      } @else if (ventures().length === 0) {
        <div class="empty-state">
          <p>No ventures yet. Create your first one!</p>
        </div>
      } @else {
        <div class="ventures-grid">
          @for (venture of ventures(); track venture.id) {
            <div class="venture-card">
              @if (venture.logo) {
                <img [src]="venture.logo" [alt]="venture.name" />
              }
              <h3>{{ venture.name }}</h3>
              <p>{{ venture.sector }}</p>
              <span class="badge" [class.published]="venture.is_published">
                {{ venture.is_published ? 'Published' : 'Draft' }}
              </span>
              <div class="actions">
                <a [routerLink]="['/dashboard/ventures', venture.slug]">View</a>
                <a [routerLink]="['/dashboard/ventures/edit', venture.slug]">Edit</a>
                <button (click)="deleteVenture(venture.id)">Delete</button>
              </div>
            </div>
          }
        </div>

        @if (hasMorePages()) {
          <div class="pagination">
            <button (click)="loadMore()" [disabled]="venturesService.isLoading()">
              Load More
            </button>
          </div>
        }
      }
    </div>
  `
})
export class VenturesListComponent implements OnInit {
  venturesService = inject(VenturesService);

  currentPage = signal(1);
  ventures = this.venturesService.ventures;
  totalCount = this.venturesService.totalCount;

  hasMorePages = computed(() => {
    return this.ventures().length < this.totalCount();
  });

  ngOnInit(): void {
    this.loadVentures();
  }

  loadVentures(): void {
    this.venturesService.getMyVentures(this.currentPage()).subscribe();
  }

  loadMore(): void {
    this.currentPage.update(page => page + 1);
    this.loadVentures();
  }

  deleteVenture(id: string): void {
    if (confirm('Are you sure you want to delete this venture?')) {
      this.venturesService.deleteVenture(id).subscribe();
    }
  }
}

